import { canUseDOM } from '@dnd-kit-svelte/utilities';
export function useSensorSetup(sensorsFn) {
    $effect(() => {
        if (!canUseDOM) {
            return;
        }
        const sensors = sensorsFn();
        const teardownFns = sensors.map(({ sensor }) => sensor.setup?.());
        return () => {
            for (const teardown of teardownFns) {
                teardown?.();
            }
        };
    });
}
