import { box } from 'svelte-toolbelt';
export function useSyntheticListeners(args) {
    return box.with(() => {
        const [listeners, id] = args();
        return listeners.reduce((acc, { eventName, handler }) => {
            acc[eventName] = (event) => {
                handler(event, id);
            };
            return acc;
        }, {});
    });
}
