import { isWindow } from '@dnd-kit-svelte/utilities';
export function getScrollXCoordinate(element) {
    if (isWindow(element)) {
        return element.scrollX;
    }
    return element.scrollLeft;
}
export function getScrollYCoordinate(element) {
    if (isWindow(element)) {
        return element.scrollY;
    }
    return element.scrollTop;
}
export function getScrollCoordinates(element) {
    return {
        x: getScrollXCoordinate(element),
        y: getScrollYCoordinate(element),
    };
}
