export function hasSortableData(entry) {
    if (!entry) {
        return false;
    }
    const data = entry.data;
    if (data &&
        'sortable' in data &&
        typeof data.sortable === 'object' &&
        'containerId' in data.sortable &&
        'items' in data.sortable &&
        'index' in data.sortable) {
        return true;
    }
    return false;
}
